mapper.select:
	select * from (select rownum rnum, noticeno, noticetitle,
	 noticedate, noticewriter, noticecontent, original_filepath,
	 rename_filepath, importance, imp_end_date, readcount from
	 	(select * from notice order by importance desc,
	 	noticedate desc, noticeno desc)
    ) where rnum between #{startRow} and #{endRow}

		select * from (select rownum rnum, noticeno, noticetitle, noticedate, 
		noticewriter, noticecontent, original_filepath, rename_filepath, 
        importance, imp_end_date, readcount 
        	from (select * from notice 
        		where noticedate between #{begin} and #{end}
        		order by importance desc, imp_end_date asc, noticeno desc)
        	     ) 
        	     where rnum between #{startRow} and #{endRow}

update.if:
update notice set noticetitle = #{noticeTitle}, noticewriter = #{noticeWriter}, 
	noticecontent = #{noticeContent}, noticedate = sysdate, 			original_filepath = null, rename_filepath = null, 			importance = #{importance}, imp_end_date = #{impEndDate} 
	where noticeno = #{noticeNo}

		update notice set noticetitle = #{noticeTitle}, noticewriter = #{noticeWriter}, 
			noticecontent = #{noticeContent}, noticedate = sysdate, 			original_filepath = #{originalFilePath}, rename_filepath = #{renameFilePath},
 			importance = #{importance}, imp_end_date = #{impEndDate} 
			where noticeno = #{noticeNo}
