DROP TABLE IF EXISTS `ITEM`;

CREATE TABLE `ITEM` (
  `ITEM_ID` INT(255) unsigned NOT NULL PRIMARY KEY,
  `ITEM_TYPE` VARCHAR(300) CHARACTER SET utf8mb4 COLLATE UTF8MB4_BIN DEFAULT '',
  `ITEM_DATA` VARCHAR(300) ,
  `ITEM_NAME` VARCHAR(300) NOT NULL,
  `PRICE` INT(255) NOT NULL,
  `GAMEPRICE` INT(255) NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

commit;
